import axios from "axios";

import { Link } from "react-router-dom";
import "./Coment.css"
export function Comentario(props){

//up

  console.log(props.index+props.userName+props.comentario+props.id)

  async function handleDelete() {
    try {
      await axios.delete(
        `https://ironrest.herokuapp.com/apiDICKvigarista/${props.id}`
      );
      props.setPosted(true);
    } catch (error) {
      console.error(error);
    }
  }

  return (
    <div className="commentbox" key={props.index}>
      <div className="conteudo">
       <h1>{props.userName}</h1> 
       <hr/>
       <p>{props.comentario}</p>
      <button className="botao" type="button" onClick={handleDelete}>Deletar</button>
      <Link to={props.id} ><button className="botao" type="button" >EDITAR</button></Link>
      </div>
      </div>
  )

}
